$(document).ready(function(){

    //tabsscroll active
    $("li[data-tab=tab-4]").click(function(){
        $(".h-scroll--tabs").scrollLeft(250);
    });  
    $("li[data-tab=tab-3]").click(function(){
        $(".h-scroll--tabs").scrollLeft(0);
    });  



    //carousel
    $(".box-modal__carousel").slick({
        dots: true,
        infinite: true,
        slidesToShow: 2,
        slidesToScroll: 1
    });
    //carousel
    $(".carousel-product__items").slick({
        dots: true,
        infinite: true,
        slidesToShow: 4,
        slidesToScroll: 1
    });
	//carousel
	$(".carousel-certeficate__items").slick({
		dots: true,
		infinite: true,
		slidesToShow: 2,
		slidesToScroll: 1
	});
	$(".popular-categories__items").slick({
		dots: true,
		infinite: true,
		slidesToShow: 5,
		slidesToScroll: 1
	});


    //product tabs
    $('.products-tabs__item').click(function(){
        var tab_id = $(this).attr('data-tab');

        $('.products-tabs__item').removeClass('active');
        $('.product-tabs-list').removeClass('active');

        $(this).addClass('active');
        $("#"+tab_id).addClass('active');
    });


    //card tabs
    $('.card-tabs__item').click(function(){
        var tab_id = $(this).attr('data-tab');

        $('.card-tabs__item').removeClass('active');
        $('.card__tabs-list').removeClass('active');

        $(this).addClass('active');
        $("#"+tab_id).addClass('active');
    });


	//map tabs
	$('.adress__link').click(function(){
		var tab_id = $(this).attr('data-tab');

		$('.adress__link').removeClass('active');
		$('.map-location').removeClass('active');

		$(this).addClass('active');
		$("#"+tab_id).addClass('active');
	});






  

});


